# phylogenetic analysis (funannotate compare)

# download data from NCBI and prepare for analysis

funannotate util gff2tbl -g <(zcat Birgus.latro.genome.gff.gz) -f <(zcat Birgus.latro.genome.fasta.gz) > Birgus.latro.tbl
funannotate util tbl2gbk -i Birgus.latro.tbl -f <(zcat Birgus.latro.genome.fasta.gz) -s "Birgus latro" --isolate Birgus 


funannotate util gff2tbl -g <(zcat Panulirus.ornatus.genome.gff.gz) -f Panulirus.ornatus.genome.fasta > Panulirus.ornatus.tbl
funannotate util tbl2gbk -i Panulirus.ornatus.tbl -f Panulirus.ornatus.genome.fasta -s "Panulirus ornatus" --isolate ornatus 


funannotate util gff2tbl -g <(zcat Paralithodes.camtschaticus.genome.gff.gz) -f Paralithodes.camtschaticus.genome.fasta > Paralithodes.camtschaticus.tbl
funannotate util tbl2gbk -i Paralithodes.camtschaticus.tbl -f Paralithodes.camtschaticus.genome.fasta -s "Paralithodes camtschaticus" --isolate camtschaticus 


funannotate util gff2tbl -g <(zcat Additional_file_4_Bachvaroff_Csap_CDS.GFF.gz) -f Additional_file_1_Bachvaroff_Csap_genome.fasta > Callinectes.sapidus.tbl
funannotate util tbl2gbk -i Callinectes.sapidus.tbl -f Additional_file_1_Bachvaroff_Csap_genome.fasta -s "Callinectes sapidus" --isolate Callinectes 


funannotate util gff2tbl -g GCF_017591435.1_ASM1759143v1_genomic.gff -f GCF_017591435.1_ASM1759143v1_genomic.fna > Portunus.trituberculatus.tbl
funannotate util tbl2gbk -i Portunus.trituberculatus.tbl -f GCF_017591435.1_ASM1759143v1_genomic.fna -s "Portunus trituberculatus" --isolate Portunus 


funannotate util gff2tbl -g GCF_024679095.1_ASM2467909v1_genomic.gff -f GCF_024679095.1_ASM2467909v1_genomic.fna > Eriocheir.sinensis.tbl
funannotate util tbl2gbk -i Eriocheir.sinensis.tbl -f GCF_024679095.1_ASM2467909v1_genomic.fna -s "Eriocheir sinensis" --isolate Eriocheir


funannotate util gff2tbl -g GCA_016584305.1_ASM1658430v1_genomic.gff -f GCA_016584305.1_ASM1658430v1_genomic.fna > Chionoecetes.opilio.tbl
funannotate util tbl2gbk -i Chionoecetes.opilio.tbl -f GCA_016584305.1_ASM1658430v1_genomic.fna -s "Chionoecetes opilio" --isolate Chionoecetes


# run analysis
